import React, {useEffect} from 'react';
import { Card, Form, Input, Typography, Button, Layout} from "antd";
import styles from './styles';
import FileBase64 from 'react-file-base64';
import { createStory,updateStory } from '../../actions/stories';
import { useSelector,useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { HomeOutlined} from "@ant-design/icons";
const {Title} = Typography;

import {useNavigate } from 'react-router-dom'

function StoryForm({selectedId, setSelectedId}) {

  const story = useSelector((state) => selectedId ? state.stories.find(story => story._id === selectedId) : null);
  const disptach = useDispatch();
  const navigate = useNavigate();
  const [form] = Form.useForm();
  
  const user = JSON.parse(localStorage.getItem("profile"));
  const username = user?.result?.username;

  const onSubmit = (formValues) => {
    selectedId ? 
    disptach(updateStory(selectedId, {...formValues,username})) 
    : disptach(createStory({...formValues,username}));

    reset();
  };

  useEffect(()=> {
    if(story){
      form.setFieldsValue(story);
    }
  }, [story,form]);

  const reset = () => {
  form.resetFields();
  setSelectedId(null);
  }

  if (!user) {
    return (
      <Layout style={styles.layout}>
          <Card style={styles.formCard}>
        <Title level={4}>
          <span style={styles.formTitle}>
            Welcome to FlickerFeed!
          </span> <br />
          Please <Link to="/authform">login</Link> or{" "}
          <Link to="/authform">register</Link>  for sharing instant moments or ideas.
        </Title>
      </Card>
      </Layout>
    );
  }


  return (
    <Layout style={styles.layout}>
     
    <Card 
    style={styles.formCard} 
    title={
    <Title
    level={4} style={styles.formTitle}
    >
       <Button style={styles.returnBtn} onClick={Navigate()}>
       <HomeOutlined />
      </Button>
      {selectedId ? "Editing" : "Share"} a story
    </Title>}>

      <Form
      form={form}
      labelCol={{span: 6}}
      wrapperCol={{span:16}}
      layout="horizontal"
      size="middle"
      onFinish={onSubmit}>

        <Form.Item name="caption" label="Caption" rules={[{required:true}]}>
          <Input.TextArea allowClear autoSize={{minRows:2, maxRows:6}}/>
        </Form.Item>
        <Form.Item name="tags" label="Tags" >
        <Input.TextArea allowClear autoSize={{minRows:2, maxRows:6}}/>
        </Form.Item>
        <Form.Item name="image" label="Image" rules={[{required:true}]}>
          <FileBase64
          type="file"
          multiple={false}
          onDone={(e)=>{
            form.setFieldsValue({
              image:e.base64
            })
          }}
          />
        </Form.Item>
        <Form.Item
        wrapperCol={{
          span:16,
          offset:6
        }}>
          <Button
          type="primary"
          htmlType='submit'
          style={styles.button}>
            Share
          </Button>
        </Form.Item>

        {!selectedId ? null :
        <Form.Item
        wrapperCol={{
          span:16,
          offset:6
        }}>
          <Button
          type="primary"
          htmlType='button'
          danger
          onClick={reset}
          style={styles.button}>
            Discard
          </Button>
        </Form.Item>
        }
      </Form>
    </Card>
  </Layout>

  )
}

export default StoryForm;
