import * as api from "../api";

const getStories = () => async (dispatch) => {
    try {
        const { data } = await api.fetchStories();
        const action = { type:"FETCH_ALL", payload: data};
        dispatch(action);
    } catch (error) {
        console.log(error)
    }

};

export default getStories;
