import React, {useEffect, useState} from "react";
import { useDispatch } from "react-redux";
import StoryList from "../StoryList";
import StoryForm from "../StoryForm";
import {Button, Layout} from "antd";
import styles from "./styles";
import {getStories} from "../../actions/stories";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const {Sider,Content} = Layout;

const Home = () => {
    const [selectedId, setSelectedId] = useState(null);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getStories());
    }, [dispatch]);
    return (   
    //     <Layout>
    //     <Sider style={styles.sider} width={400}>
    //         <StoryForm selectedId={selectedId} setSelectedId={setSelectedId} />
    //     </Sider>
    //     <Content style={styles.content}>
    //         <StoryList setSelectedId={setSelectedId} />
    //     </Content>
    // </Layout>
    <>
    <Layout>
        
        <Content>
                <Row>
                    <Col sm={6}><StoryList setSelectedId={setSelectedId} /></Col>
                </Row>
            </Content>
            <div>
                <p>Want to post? Click the Button below</p>
                <Button>
                <div>
        <Container style={styles.grid}>
                <Row>
                <Col sm={6}><StoryForm selectedId={selectedId} setSelectedId={setSelectedId} /></Col>
                </Row>
            </Container>
        </div>
                </Button>
            </div>
    </Layout> 
    </>
    )
}

export default Home;
