import React, {useEffect} from 'react';
import { Card, Form, Input, Typography, Button} from "antd";
import styles from './styles';
import FileBase64 from 'react-file-base64';
import { createStory,updateStory } from '../../actions/stories';
import { useSelector,useDispatch } from 'react-redux';

const {Title} = Typography;


function StoryForm({selectedId, setSelectedId}) {

  const story = useSelector((state) => selectedId ? state.stories.find(story => story._id === selectedId) : null);
  const disptach = useDispatch();
  const [form] = Form.useForm();

  const onSubmit = (formValues) => {
    selectedId ? 
    disptach(updateStory(selectedId, formValues)) 
    : disptach(createStory(formValues));
  };

  useEffect(()=> {
    if(story){
      form.setFieldsValue(story);
    }
  }, [story,form]);

  return (
  <Card 
  style={styles.formCard} 
  title={
  <Title
  level={4} style={styles.formTitle}>
    {selectedId ? "Editing" : "Share"} a story
  </Title>}>

    <Form
    form={form}
    labelCol={{span: 6}}
    wrapperCol={{span:16}}
    layout="horizontal"
    size="middle"
    onFinish={onSubmit}>

      <Form.Item name="username" label="Username" rules={[{required:true}]}>
        <Input allowClear/>
      </Form.Item>
      <Form.Item name="caption" label="Caption" rules={[{required:true}]}>
        <Input.TextArea allowClear autoSize={{minRows:2, maxRows:6}}/>
      </Form.Item>
      <Form.Item name="tags" label="Tags" >
      <Input.TextArea allowClear autoSize={{minRows:2, maxRows:6}}/>
      </Form.Item>
      <Form.Item name="image" label="Image" rules={[{required:true}]}>
        <FileBase64
        type="file"
        multiple={false}
        onDone={(e)=>{
          form.setFieldsValue({
            image:e.base64
          })
        }}
        />
      </Form.Item>
      <Form.Item
      wrapperCol={{
        span:16,
        offset:6
      }}>
        <Button
        type="primary"
        block
        htmlType='submit'>
          Share
        </Button>
      </Form.Item>
    </Form>
  </Card>
  )
}

export default StoryForm;
